#include <iostream>

using namespace std;

int countBalance(char* s, int hard, int balance) {
    if (*s == '\0') return hard + balance;
    if (*s == ')') {
        if (balance <= 0) hard++;
        else balance--;
    }
    if (*s == '(') {
        balance++;
    }
    return countBalance(s + 1, hard, balance);
}

int mininumBracketAdd(string s) {
    // STUDENT ANSWER
    return countBalance(&s[0], 0, 0);
}

